import React, {useState, useEffect} from 'react';
import reactLogo from "../../1200px-React-icon.svg.png";
import styles from "../../App.style";

const rotatingLogo = () => {
    let [logoAngle,setLogoAngle] = useState(0);

    setInterval(() => {
        setLogoAngle(logoAngle + 1);
        if(logoAngle === 180)
            setLogoAngle(0);
        console.log(logoAngle)
    },500);

    return(
        <img alt={"reactLogo"} src={reactLogo} style={{objectFir: 'cover', transform: 'rotate('+logoAngle+'deg)'}}/>
    )
};

export default rotatingLogo;
