import React from 'react';
import styles from "../../App.style";
import cppLogo from "../../images/skills/1200px-ISO_C++_Logo.svg.png";

class SkillChip extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            mouseOn : false,
        }
    }

    handleMouseOver(){
        this.setState({
            mouseOn : true
        })
    }

    handleMouseLeave(){
        this.setState({
            mouseOn:false,
        })
    }

    render() {
        return(
            <div style={this.state.mouseOn ? styles.skillMouseOver : styles.skill} onMouseEnter={this.handleMouseOver} onMouseLeave={this.handleMouseLeave}>
                <img src={this.props.image} alt={"logo"}/>
                <div style = {{height: '1em', width:'80%', border:'1px solid rgb(110, 212, 111)'}}>
                    <div/>
                </div>
            </div>
        );
    }
}

export default SkillChip;
