let styles = {
    skill: {
        textAlign:'center',
        width:'10em',
        height: '10em',
        border : '2px solid rgba(110, 212, 111,255)',
        borderRadius: '5px',
        margin : '3%',
        paddingTop: '1em',
        transition:'all .5s',
    },

    skillMouseOver: {
        textAlign:'center',
        width:'10em',
        height: '10em',
        border : '3px solid rgba(110, 212, 111,255)',
        borderRadius: '5px',
        margin : '3%',
        paddingTop: '1em',
        boxShadow: '5px 5px 10px rgba(60, 162, 61,100)',
        transition:'all .5s',
    },

    skillLogo: {
        height:'5em',
        width:'5em',
        marginLeft:'auto',
        marginRight:'auto',
        backgroundSize:'cover',
        backgroundPosition:'center',
        marginBottom:'1em'
    },

    level: {
        width:'90%',
        height: '1em',
        border: '1px solid rgba(110, 212, 111,255)',
        marginLeft:'auto',
        marginRight:'auto',
    }
};

export default styles;
