import photo from "./ma_photo.png"

let styles = {
    app: {
        marginTop : '0px',
    },

    header: {
        backgroundColor : '#272d3a',
        width:'100%',
        height: '10%',
        minHeight: '16em',
        backgroundSize:'cover',
        backgroundPosition:'center',
        display: 'flex',
        zIndex:0,
        overflow:'hidden'
    },

    logoContainer:{
        height: '16em',
        overflow: 'hidden',
        marginLeft:'50%'
    },

    logo : {
        objectFit:'cover',
    },

    infosContainer: {
        color:'#ffffff',
        textShadow : '10px 5px 5px #000000',
        justifySelf:'center',
        alignSelf:'center',
        marginLeft : '10%',
        marginTop: '3%',
        position:'absolute',
        display:'flex',
    },

    photo: {
        backgroundImage: 'url(' + photo + ')',
        backgroundSize:'cover',
        backgroundPosition: 'center',
        height: '15em',
        width: '10em',
        backgroundRepeat: 'no-repeat',
        boxShadow : '10px 5px 5px #000000',
        marginRight: '10%'
    },

    name:{
        fontFamily:'Oswald, sans-serif',
        fontSize:'3em',
        marginBottom:'0px',
        marginTop:'0px',
        color:'rgb(167,222,255)'
    },

    job : {
        fontFamily:'Roboto, sans-serif',
        fontSize: '1.8em',
        marginTop:'0px'
    },

    linkIconsContainer : {
        marginLeft:'auto',
        alignSelf: 'flex-end',
        padding: '5px',
        display:'flex',
        justifyContent:'space-around',
        width:'20%',
        minWidth:'15em',
        borderTopLeftRadius: '5px',
        borderTopRightRadius: '5px'
    },

    linkIcon : {
        width: '3.5em',
        height: '3.5em'
    },
};

export default styles;
