import React from 'react';
import styles from "./skillChip.styles.js";
import cppLogo from "../../images/skills/1200px-ISO_C++_Logo.svg.png";

class SkillChip extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            mouseOn : false,
        };
        this.handleMouseLeave = this.handleMouseLeave.bind(this);
        this.handleMouseOver = this.handleMouseOver.bind(this);
    }

    handleMouseOver(){
        this.setState({
            mouseOn : true
        })
    }

    handleMouseLeave(){
        this.setState({
            mouseOn:false,
        })
    }

    render() {
        return(
            <div style={this.state.mouseOn ? styles.skillMouseOver : styles.skill} onMouseEnter={this.handleMouseOver} onMouseLeave={this.handleMouseLeave}>

            </div>
        );
    }
}

export default SkillChip;
