import headerbg from "./Interview-React-2.jpg";
import photo from "./ma_photo.png"

let styles = {
    app: {
        marginTop : '0px',
    },

    header: {
        backgroundImage:'url('+headerbg+')',
        width:'100%',
        height: '10%',
        minHeight: '16em',
        backgroundSize:'cover',
        backgroundPosition:'center',
        display: 'flex',
    },

    infosContainer: {
        color:'#ffffff',
        textShadow : '10px 5px 5px #000000',
        justifySelf:'center',
        alignSelf:'center',
        marginLeft : '10%',
        marginTop: '3%',
        position:'absolute',
        display:'flex',
    },

    photo: {
        backgroundImage: 'url(' + photo + ')',
        backgroundSize:'cover',
        backgroundPosition: 'center',
        height: '15em',
        width: '10em',
        backgroundRepeat: 'no-repeat',
        boxShadow : '10px 5px 5px #000000',
        marginRight: '10%'
    },

    name:{
        fontFamily:'Oswald, sans-serif',
        fontSize:'3em',
        marginBottom:'0px',
        marginTop:'0px',
        color:'rgb(167,222,255)'
    },

    job : {
        fontFamily:'Roboto, sans-serif',
        fontSize: '1.8em',
        marginTop:'0px'
    },

    linkIconsContainer : {
        marginLeft:'auto',
        alignSelf: 'flex-end',
        backgroundColor:'rgba(255,255,255,0.19)',
    },

    linkIcon : {
        width: '4em',
        height: '4em'
    },
};

export default styles;
