let styles = {
    container: {
        width: '80%',
        height: 'fit-content',
        border:'2px solid rgba(196, 31, 56,255)',
        marginTop:'1em',
        marginBottom:'1em',
        transition:'all .5s',
    },

    containerMouseOver : {
        width: '80%',
        height: 'fit-content',
        border:'3px solid rgba(196, 31, 56,255)',
        marginTop:'1em',
        marginBottom:'1em',
        boxShadow: '5px 5px 10px rgba(196, 31, 56,255)',
        transition:'all .5s',
    },

    title : {
        fontFamily:'Oswald, sans-serif',
        fontSize:'2em'
    },

    description: {
        fontFamily: 'Roboto, sans-serif',
    }

};
export default styles;
