import headerbg from "./small-memory-lp.jpg";
import photo from "./ma_photo.png"

let styles = {
    app: {
        marginTop : '0px',
    },

    header: {
        backgroundImage:'url('+headerbg+')',
        width:'100%',
        height: '10%',
        minHeight: '16em',
        backgroundSize:'cover',
        backgroundPosition:'center',
        display:'flex',
    },

    infosContainer: {
        color:'#ffffff',
        textShadow : '10px 5px 5px #000000',
        justifySelf:'center',
        alignSelf:'center'
    },

    photo: {
        position:'absolute',
        backgroundImage: 'url(' + photo + ')',
        backgroundSize:'cover',
        backgroundPosition: 'center',
        height: '15em',
        width: '10em',
        backgroundRepeat: 'no-repeat',
        marginLeft : '10%',
        marginTop: '5%',
        boxShadow : '10px 5px 5px #000000',
    },



    name:{
        fontFamily:'Oswald, sans-serif',
        fontSize:'3em',
        marginBottom:'0px'
    },

    job : {
        fontFamily:'Roboto, sans-serif',
        fontSize: '1.8em',
        marginTop:'0px'
    },
};

export default styles;
